import React from 'react';
import styled from 'styled-components';
import { useWindowResize } from '../../../components/customHooks/index';
import SearchTag from '../../../components/searchTag/index';
import Checkbox from '../../../components/checkbox/index';
import Dropdown from '../../../components/dropdown/index';
import TextInput from '../../../components/textInput/index';
import FilledButton from '../../../components/filledButton/index';
import { inputData, tagData } from './data';
const ProfileCreation1 = () => {
  const size = useWindowResize();
  const tags = tagData.map((tagName, index) => {
    return (
      <TagContainer>
        <SearchTag text={tagName} key={`tag-${index}`} />
      </TagContainer>
    );
  });

  const inputs = inputData.map((item, index) => {
    const key = Object.keys(item)[0];
    return (
      <InputContainer key={`input-${index}`}>
        <TextInput
          compulsory
          width='100%'
          multiline={item[key].multiline}
          height={item[key].height}
          characterMax={item[key].characterMax}
          labelHeader={item[key].labelHeader}
          labelDesc={item[key].labelDesc}
          identifier={key}
        />
      </InputContainer>
    );
  });
  const colData = ['col1', 'col2'];
  console.log(size.width)
  const cols = colData.map(colName => {
    if (colName === 'col1')
      return (
        <Column>
          <TagsContainer>{tags}</TagsContainer>
        </Column>
      );
    else
      return (
        <Column left>
          {inputs}
          <ButtonContainer>
            <FilledButton text='Next' path='profile-creation/2' />
          </ButtonContainer>
        </Column>
      );
  });

  return <StyledBody>{cols}</StyledBody>;
};

const StyledBody = styled.main`
  display: grid;
  grid-template-columns: 30% 70%;
  padding-top: 1rem;
  @media only screen and (max-width: 768px) {
    grid-template-rows: 50% 50% !important;
    grid-template-columns: none;
    padding-top: 0.5rem;
  }
`;

const Column = styled.div`
  width: 100%;
  padding-left: ${props => (props.left ? '2.5rem' : '0rem')};
  @media only screen and (max-width: 768px) {
    padding-left: 0;
  }
`;
const TagsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  max-height: 50vh;
  overflow-y: auto;
`;

const TagContainer = styled.div`
  margin: 0.3rem;
`;

const InputContainer = styled.div`
  margin-bottom: 1.65rem;
`;

const ButtonContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: flex-end;
`;

export default ProfileCreation1;
