import React, { useEffect } from 'react';
import styled from 'styled-components';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  withRouter,
  useLocation
} from 'react-router-dom';
import ProfileCreation1 from './ProfileCreation1/index';
import ProfileCreation2 from './ProfileCreation2';
import WebsiteTitle from '../../components/websiteTitle/index';
import Logout from '../../components/logout/index';
import FilledButton from '../../components/filledButton/index';
const ProfileCreationMaster = () => {
  const location = useLocation();
  useEffect(() => {
    console.log(location);
  }, [location]);
  return (
    <PageContainer>
      <StyledHeader>
        <LogoContainer>
          <WebsiteTitle />
        </LogoContainer>
        <Logout />
      </StyledHeader>
      <SetUpClubProfile />
      <PageDesc>
        {location.pathname.includes('/profile-creation/2')
          ? 'Get-to-Know-Yous: Why Students Should Join'
          : 'Opening Statement: Provide Basic Info'}
      </PageDesc>
      <Router>
        <Switch>
          <Route path='/profile-creation' exact component={ProfileCreation1} />
          <Route path='/profile-creation/2' component={ProfileCreation2} />
        </Switch>
        <ButtonContainer>
          <FilledButton
            text={
              window.location.pathname === '/profile-creation/2'
                ? 'Make my club profile'
                : 'Next'
            }
            path='/profile-creation/2'
          />
        </ButtonContainer>
      </Router>
    </PageContainer>
  );
};

export default withRouter(ProfileCreationMaster);

const SetUpClubProfile = () => {
  return (
    <section style={{ paddingLeft: '0.3rem' }}>
      <ClubProfileTitle>Set Up Your Club Profile</ClubProfileTitle>
      <Subtext>Complete your profile for maximum outreach</Subtext>
    </section>
  );
};
const ClubProfileTitle = styled.h1`
  font-family: 'Manrope', 'Roboto', 'Arial', 'Helvetica';
  font-size: 2.25rem;
  font-weight: 700;
  margin-bottom: 0;
  @media only screen and (max-width: 600px) {
    font-size: 1.5rem !important;
  }
`;

const PageContainer = styled.div`
  display: grid;
  grid-template-rows: auto auto auto;
  padding: 0 4.375rem;
`;

const StyledHeader = styled.header`
  display: flex;
  justify-content: space-between;
`;

const LogoContainer = styled.div`
  padding-left: 0.3rem;
`;

const PageDesc = styled.h2`
  font-family: 'Roboto', 'Arial', 'Helvetica';
  font-weight: 400;
  padding-left: 0.3rem;
  @media only screen and (max-width: 600px) {
    font-size: 1rem !important;
  }
`;

const Subtext = styled.h3`
  color: ${props => props.theme.colors.gray};
  font-family: 'Roboto', 'Arial', 'Helvetica';
  margin-top: 0.5rem;
  font-weight: 500;
  @media only screen and (max-width: 600px) {
    font-size: 0.875rem !important;
  }
`;

const ButtonContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: flex-end;
`;
