import React, { cloneElement } from 'react';

const GrayTagContainer = ({ children }) => {
  const [selectedTag, setSelectedTag] = useState('');
  return <>{(cloneElement(children), { selectedTag, setSelectedTag })}</>;
};

export default GrayTagContainer;
