import React from 'react'
import styled from 'styled-components'
import {motion} from 'framer-motion'

const GrayTag = () => {

  const tagVariants = {
    active: { color: theme.colors.white, backgroundColor: theme.colors.red },
    inactive: {
      color: theme.colors.red,
      backgroundColor: 'rgba(236, 108, 82, 0.08)',
    },
  };

  return(
    <Tag>test</Tag>
  );
}

const Tag = styled(motion.button)`
  display: inline-block;
  padding: 0.4rem 1rem;
  font-family: 'Roboto', 'Arial', 'Helvetica';
  font-size: 1rem;
  border-radius: 1rem;
  border: 0.125rem ${(props) => props.theme.colors.gray} solid;
  outline-color: ${(props) => props.theme.colors.blue};
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  cursor: pointer;
  @media only screen and (max-width: 600px) {
    font-size: 0.85rem;
  }
`;

export default GrayTag
