import React, { useState, useRef, useEffect } from 'react';
import styled, { withTheme } from 'styled-components';
import { motion } from 'framer-motion';
import { useFocused, useViewport } from '../customHooks/index';

const Checkbox = ({ theme, labelText }) => {
  const [clicked, setClicked] = useState(false);
  const checkbox = useRef(null);
  const checkboxFocused = useFocused(checkbox);
  const { width } = useViewport();
  const breakpoint = 600;

  const onKeypress = (e) => {
    if (e.keyCode === 13) {
      checkbox.current.click();
    }
  };

  useEffect(() => {
    if (checkboxFocused) {
      document.addEventListener('keypress', onKeypress);
    }
    return () => {
      document.removeEventListener('keypress', onKeypress);
    };
  }, [checkboxFocused]);

  return (
    <LabelContainer>
      <CheckboxContainer
        tabIndex={0}
        ref={checkbox}
        onClick={() => {
          setClicked(!clicked);
        }}
      >
        <StyledCheckbox clicked={clicked}>
          {width <= breakpoint ? <span>{labelText}</span> : <></>}
          {clicked && width > breakpoint ? (
            <Check
              width='14'
              height='11'
              viewBox='0 0 14 11'
              fill='none'
              xmlns='http://www.w3.org/2000/Check'
            >
              <path
                d='M13 1.5L4.75 9.75L1 6'
                stroke={theme.colors.white}
                strokeWidth={2}
              />
            </Check>
          ) : (
            <></>
          )}
        </StyledCheckbox>
      </CheckboxContainer>
      {width <= breakpoint ? (
        <></>
      ) : (
        <CheckboxLabel
          onClick={() => {
            setClicked(!clicked);
          }}
        >
          {labelText}
        </CheckboxLabel>
      )}
    </LabelContainer>
  );
};

const LabelContainer = styled.div`
  display: flex;
  align-items: center;
`;

const CheckboxContainer = styled.div`
  display: inline-block;
  vertical-align: middle;
  outline-color: ${(props) => props.theme.colors.blue};
`;

const StyledCheckbox = styled(motion.div)`
  width: 1.25rem;
  height: 1.25rem;
  border-radius: 0.3125rem;
  background-color: ${(props) =>
    props.clicked
      ? props.theme.colors.checkboxGray
      : props.theme.colors.fullWhite};
  border: 0.03125rem solid
    ${(props) =>
      props.clicked
        ? props.theme.colors.checkboxGray
        : props.theme.colors.gray};
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  @media screen and (max-width: 600px) {
    width: 6.6rem;
    height: 2rem;
    border-radius: 0;
  }
`;

const CheckboxLabel = styled.label`
  color: ${(props) => props.theme.colors.checkboxGray};
  font-size: 1.125rem;
  font-family: 'Roboto', 'Arial', 'Helvetica';
  margin-left: 0.65rem;
  -webkit-user-select: none;
  -moz-user-select: none;
  height: auto;
  width: auto;
  -ms-user-select: none;
  cursor: pointer;
`;

const Check = styled.svg`
  width: 14px;
  height: 11px;
  fill: none;
`;

export default withTheme(Checkbox);
