import React from 'react';
import styled from 'styled-components';

import SearchTag from '../../components/searchTag/index';
import Checkbox from '../../components/checkbox/index';
import Dropdown from '../../components/dropdown/index';
import TextInput from '../../components/textInput/index';
import FilledButton from '../../components/filledButton/index';
const ProfileCreation1 = () => {
  const inputData = [
    {
      header: {
        labelName: 'Club Name:',
        labelDesc: null,
        height: '2.635rem'
      }
    },
    {
      shortDesc: {
        labelName: 'Short Description:',
        labelDesc: '400 characters max',
        height: '6.135rem'
      }  
    },
    {
      longDesc: {
        labelName: 'Long Description:',
        labelDesc: '800 characters max',
        height: '10.125rem'
      }  
    }
  ];
  const inputs = inptuData.map(() => {
    return (
      <InputContainer>
        <TextInput />
      </InputContainer>
    );
  });

  return (
    <StyledBody>
      <Column>
        <div>test1</div>
      </Column>
      <Column>
        <InputContainer>
          <TextInput
            labelHeader='Club Name:'
            compulsory
            width='100%'
            height='2.625rem'
          />
        </InputContainer>
        <InputContainer>
          <TextInput
            labelHeader='Short Description:'
            labelDesc='300 characters max'
            compulsory
            multiline
            width='100%'
            height='6.135rem'
          />
        </InputContainer>

        <FilledButton text='Next' path='profile-creation/2' />
      </Column>
    </StyledBody>
  );
};

const StyledBody = styled.main`
  display: grid;
  grid-template-columns: 50% 50%;
  padding-top: 3rem;
  @media only screen and (max-width: 768px) {
    grid-template-rows: 100% 100% !important;
    grid-template-columns: none;
  }
`;

const Column = styled.div`
  width: 100%;
`;

const InputContainer = styled.div`
  margin-bottom: 1.65rem;
`;

export default ProfileCreation1;
