import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const GrayTag = () => {
  const [clicked, setClicked] = useState(false);
  const tagVariants = {
    active: {
      borderWidth: '2px',
      backgroundColor: 'rgba(226, 223, 223, 0.53)',
      margin: '-1px',
    },
    inactive: {
      borderWidth: '1px',
      backgroundColor: 'rgba(226, 223, 223, 0)',
      margin: '0px',
    },
  };

  return (
    <Tag
      onClick={() => {
        setClicked(!clicked);
      }}
      variants={tagVariants}
      clicked={clicked}
      initial='inactive'
      animate={clicked ? 'active' : 'inactive'}
    >
      test
    </Tag>
  );
};

const Tag = styled(motion.button)`
  display: inline-block;
  padding: 0.4rem 1rem;
  font-family: 'Roboto', 'Arial', 'Helvetica';
  font-size: 1rem;
  border-radius: 1rem;
  border: /*${(props) => (props.clicked ? '.125rem' : '0.0625rem')}*/ ${(
      props
    ) => props.theme.colors.gray}
    solid;
  outline-color: ${(props) => props.theme.colors.blue};
  -webkit-user-select: none;
  -moz-user-select: none;
  /*margin: ${(props) => (props.clicked ? '-0.0625rem' : '0')};*/
  -ms-user-select: none;
  user-select: none;
  -moz-box-sizing: border-box;
  -webkit-box-sizing: border-box;
  -ms-box-sizing: border-box;
  box-sizing: border-box;
  cursor: pointer;
  @media only screen and (max-width: 600px) {
    font-size: 0.85rem;
  }
`;

export default GrayTag;
