import React, { useState, useRef, useEffect } from 'react';
import styled, { withTheme, css } from 'styled-components';
import { motion } from 'framer-motion';
import { useFocused, useViewport } from '../customHooks/index';

// orderNum is for when you want border radius on sides of mobile version

const Checkbox = ({ theme, labelText, orderNum }) => {
  const [clicked, setClicked] = useState(false);
  const checkbox = useRef(null);
  const checkboxFocused = useFocused(checkbox);
  const { width } = useViewport();
  const breakpoint = 600;

  const onKeypress = (e) => {
    if (e.keyCode === 13) {
      checkbox.current.click();
    }
  };

  useEffect(() => {
    if (checkboxFocused) {
      document.addEventListener('keypress', onKeypress);
    }
    return () => {
      document.removeEventListener('keypress', onKeypress);
    };
  }, [checkboxFocused]);

  return (
    <LabelContainer>
      <CheckboxContainer
        tabIndex={0}
        ref={checkbox}
        onClick={() => {
          setClicked(!clicked);
        }}
      >
        <StyledCheckbox clicked={clicked} orderNum={orderNum}>
          {width <= breakpoint ? (
            <MobileLabel clicked={clicked}>{labelText}</MobileLabel>
          ) : (
            <></>
          )}
          {clicked && width > breakpoint ? (
            <svg
              xmlns='http://www.w3.org/2000/svg'
              width='24'
              height='24'
              viewBox='0 0 24 24'
            >
              <path d='M9 21.035l-9-8.638 2.791-2.87 6.156 5.874 12.21-12.436 2.843 2.817z' />
            </svg>
          ) : (
            <></>
          )}
        </StyledCheckbox>
      </CheckboxContainer>
      {width <= breakpoint ? (
        <></>
      ) : (
        <CheckboxLabel
          onClick={() => {
            setClicked(!clicked);
          }}
        >
          {labelText}
        </CheckboxLabel>
      )}
    </LabelContainer>
  );
};

const LabelContainer = styled.div`
  display: flex;
  align-items: center;
  -webkit-user-select: none;
  -moz-user-select: none;
`;

const CheckboxContainer = styled.div`
  display: inline-block;
  vertical-align: middle;
  outline-color: ${(props) => props.theme.colors.blue};
`;

const MobileLabel = styled.h3`
  font-family: 'Roboto', 'Helvetica', 'Arial';
  font-size: 0.8125rem;
  font-weight: 400;
  text-align: center;
  color: ${(props) =>
    props.clicked
      ? props.theme.colors.fullWhite
      : props.theme.colors.checkboxGray};
`;

const StyledCheckbox = styled(motion.div)`
  width: 1.25rem;
  height: 1.25rem;
  border-radius: 0.3125rem;
  background-color: ${(props) =>
    props.clicked
      ? props.theme.colors.checkboxGray
      : props.theme.colors.fullWhite};
  border: 0.03125rem solid
    ${(props) =>
      props.clicked
        ? props.theme.colors.checkboxGray
        : props.theme.colors.gray};
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  @media screen and (max-width: 600px) {
    width: 6.6rem;
    height: 2rem;
    border-radius: 0;
    ${(props) =>
      props.orderNum === 0 &&
      css`
        border-top-left-radius: 2rem;
        border-bottom-left-radius: 2rem;
      `}
    ${(props) =>
      props.orderNum === 2 &&
      css`
        border-top-right-radius: 2rem;
        border-bottom-right-radius: 2rem;
      `}
  }
`;

const CheckboxLabel = styled.label`
  color: ${(props) => props.theme.colors.checkboxGray};
  font-size: 1.125rem;
  font-family: 'Roboto', 'Arial', 'Helvetica';
  margin-left: 0.65rem;
  -webkit-user-select: none;
  -moz-user-select: none;
  height: auto;
  width: auto;
  -ms-user-select: none;
  cursor: pointer;
`;

const Check = styled.svg`
  width: 14px;
  height: 11px;
  fill: none;
`;

export default withTheme(Checkbox);
