import React, { useState } from 'react';
import styled from 'styled-components';
import { FaBars } from 'react-icons/fa';
import FilledButton from '../filledButton/index';
import { NavLink } from 'react-router-dom';
import Logout from '../logout/index';
export const Navbar = () => {
  const [showLinks, setShowLinks] = useState(false);
  const [currentPath, setCurrentPath] = useState('/');
  return (
    <NavWrapper>
      <NavCenter>
        <NavHeader>
          <Logo
            onClick={() => {
              setCurrentPath('/');
            }}
          >
            <a href='/'>Clubs@CU</a>
          </Logo>
        </NavHeader>
        {currentPath === '/' && (
          <>
            <NavToggle
              onClick={() => setShowLinks(!showLinks)}
              className={`${showLinks ? 'show-container' : null}`}
            >
              <FaBars />
            </NavToggle>
            <LinksContainer
              className={`${showLinks ? 'show-container' : null}`}
            >
              <MenuLinks>
                <StyledListItem>
                  <a href='/'>Home</a>
                </StyledListItem>
                <StyledListItem>
                  <a href='/explore'>Login</a>
                </StyledListItem>
                <StyledListItem>
                  <a href='/faq'>FAQ</a>
                </StyledListItem>
                <StyledListItem>
                  <NavLink
                    style={{ textDecoration: 'none' }}
                    to='/profile-creation'
                    isActive={(match) => {
                      if (match) {
                        setCurrentPath('/profile-creation');
                      }
                    }}
                  >
                    <FilledButton text='Register Club' />
                  </NavLink>
                </StyledListItem>
              </MenuLinks>
            </LinksContainer>
          </>
        )}
        {currentPath.includes('/profile-creation') && <Logout />}
      </NavCenter>
    </NavWrapper>
  );
};

const NavWrapper = styled.nav`
  color: ${(props) => props.theme.colors.white};
  font-weight: 500;
  height: 100%;
`;

const StyledListItem = styled.li`
  display: flex;
  align-items: center;
`;

const NavCenter = styled.div`
  @media screen and (min-width: 769px) {
    padding: 2rem 3rem 0 4rem;
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
`;

const NavHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1rem;

  @media screen and (min-width: 769px) {
    padding: 0;
  }
`;

const Logo = styled.div`
  height: 100%;
  font-size: 1.5rem;
  font-family: 'Manrope', 'Arial', 'Helvetica';
  a {
    text-decoration: none;
    color: ${(props) => props.theme.colors.black};
  }
  @media (max-width: 769px) {
    padding-left: 0.5rem;
  }
`;

const NavToggle = styled.div`
  font-size: 1.3rem;
  color: black;
  background: transparent;
  border-color: transparent;
  margin-right: 1rem;
  transition: all 0.3s linear;
  cursor: pointer;

  &.show-container {
    transform: rotate(90deg);
  }

  @media screen and (min-width: 769px) {
    display: none;
  }
`;

const LinksContainer = styled.div`
  height: 0;
  overflow: hidden;
  transition: all 0.3s linear;
  background-color: white;

  &.show-container {
    height: fit-content;
  }

  @media screen and (min-width: 769px) {
    height: auto !important;
    background-color: transparent;
  }
`;

const MenuLinks = styled.ul`
  list-style-type: none;

  li {
    padding: 1rem;
    text-align: center;
  }

  a {
    margin-top: 0.4rem;
    text-decoration: none;
    color: ${(props) => props.theme.colors.black};
    font-size: 1.2rem;
    font-weight: 500;
    display: block;
    transition: color 0.2s linear;

    &:hover {
      color: ${(props) => props.theme.colors.gray};
    }
  }

  @media (min-width: 769px) {
    display: flex;
    margin: 0;
    padding: 0;
    margin-left: auto;
    color: ${(props) => props.theme.colors.black};

    a {
      font-size: 1rem;
      color: inherit;
    }
  }
`;

export default Navbar;
